import { useEffect, useState } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";

import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);
  const byDateDesc = data?.focus.sort((evtA, evtB) =>
    new Date(evtA.date) < new Date(evtB.date) ? 1 : -1
  );
  console.log(byDateDesc);
  const nextCard = () => {
    console.log('Avant:', index);
    setTimeout(
      () => setIndex((index + 1) % byDateDesc.length),
      5000
    );
    console.log('Après:', index);
  };
  useEffect(() => {
    nextCard();
  });
  return (
    <div className="SlideCardList">
      {byDateDesc?.map((event, idx) => {
        console.log(`Date pour la slide ${idx + 1}:`, event.date);
        console.log(`Mois pour la slide ${idx + 1}:`, getevent.date);
        return (
        <>
          <div
            key={event.title}
            className={`SlideCard SlideCard--${
              index === idx ? "display" : "hide"
            }`}
          >
            <img src={event.cover} alt="forum" />
            <div className="SlideCard__descriptionContainer">
              <div className="SlideCard__description">
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <div>{getMonth(new Date(event.date))}</div>
                <br />
              {new Date(event.date).toDateString()}
              </div>
            </div>
          </div>
          <div className="SlideCard__paginationContainer">
            <div className="SlideCard__pagination">
              {byDateDesc.map((eventItem, radioIdx) => (
                <input
                  key={`${eventItem.id}`}
                  type="radio"
                  name="radio-button"
                  checked={index === radioIdx}
                />
              ))}
            </div>
          </div>
        </>
        )
})}
    </div>
  );
};

export default Slider;
