export const MONTHS = {
  1: "janvier",
  2: "février",
  3: "mars",
  4: "avril",
  5: "mai",
  6: "juin",
  7: "juillet",
  8: "août",
  9: "septembre",
  10: "octobre",
  11: "novembre",
  12: "décembre",
};

export const getMonth = (date) => {
  console.log('Date dans getMonth:', date);
  const month = MONTHS[date.getMonth() + 1];
  console.log('Mois retourné:', month);
  return month;
};


